import React, { useState } from 'react';
import axios from 'axios';
export const Auth = () => { 
    return (
        <div className="auth">
            <Login />
            <Register />
        </div>
    );
}

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/auth/login', {email, password});
            console.log(response);
        } catch (error) {
            console.log(error);
        }   
    }

    return <Form username={email} setUsername={setEmail} password={password} setPassword={setPassword} Label='Login' />
}

const Register = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/auth/register', {email, password});
            console.log(response);
        } catch (error) {
            console.log(error);
        }
    }

    return <Form username={email} setUsername={setEmail} password={password} setPassword={setPassword} Label='Register' />
}
const Form = ({username, setUsername, password, setPassword, Label}) => {
    return (
        <div className='auth-container'>
            <form>
                <h1>{Label}</h1>
                <div className='form-group'>
                    <label htmlFor='username'>{label}</label>
                    <input type='text' id='username' placeholder='username' value = {username} onChange={(e) => setUsername(e.target.value)} />
                    <label htmlFor='password'>{label}</label>
                    <input type='password' id='password' placeholder='password' value={password} onChange={(e) => setPassword(e.target.value)} />

                </div>
                <button>{Label}</button>
            </form>

        </div>
    )
}
